head.script:
kFridayStudio = "Friday studio";
var kbr = "<br>";
var khr = "<hr>";

var studiocount = 1;
var lecturecount = 1;

wl = function(s) { document.write(s + "\n"); }
whr = function() { document.write("<hr>"); }

function writedayhelper(pre,cnt, date,topic,post)
{
  if (pre)
    wl(pre);
  wl("<span class='count'>" + cnt + ".</span>");
  wl("<span class='date'>" + date + "</span>");
  wl("<span class='topic'>" + topic + "</span>");
  if (post)
    wl(post);
}
function writelecturemoved(pre,date,topic)
{
  //HACK for reorganizing the calendar 
  if (pre)
    wl(pre);
  wl("<span class='count' style='color:magenta;'> &nbsp;</span>");
  wl("<span class='date' style='color:magenta;'>" + date + "</span>");
  wl("<span class='topic' style='color:magenta;'>MOVE(D): " + topic + "</span>");
}

function writelecture(pre, date, topic) 
{
  writedayhelper(pre, lecturecount,date, topic, "");
  lecturecount++;   
}
function writestudio(pre, date, topic) 
{
  s = "<span class='studio'> S" + studiocount + "</span>";
  writedayhelper(pre, s, date, topic, "");
  studiocount++;   
}

function writeNoCountLine(pre, s)
{
  wl(pre);
  wl("<span class='count'>&nbsp; </span>");
  wl("<span class='topic'>" + s + "</span>");
}

function writepsetdue(pre, date, psn)
{
    writedayhelper(pre, "", date, psetduestring(psn));
}

function psetduestring(psn)
{
   s = "<b>pset " + psn + " due</b>";
   return(s)
}

function writeweek(pre, weeknum)
{
   wl(pre);
   wl("<b>Week " + weeknum + "</b>");
}

